// App.js
import React, { useState } from 'react';
import Collapsible from './Collapsible';

function App() {
  const [output1, setOutput1] = useState('');
  const [output2, setOutput2] = useState('');
  const [customerLog, setCustomerLog] = useState(null);
  const [defaultLog, setDefaultLog] = useState(null);

  const handleFileUpload = (event, setFile) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async () => {
    const formData = new FormData();
    formData.append('customerLog', customerLog);
    formData.append('defaultLog', defaultLog);

    const response = await fetch('http://localhost:5000/upload', {
      method: 'POST',
      body: formData,
    });

    const data = await response.json();
    setOutput1(data.output1);
    setOutput2(data.output2);
  };

  function Collapsible({ title, content }) {
    const [isOpen, setIsOpen] = useState(false);

    return (
      <div>
        <button onClick={() => setIsOpen(!isOpen)}>
          {isOpen ? 'Collapse' : 'Expand'} {title}
        </button>

        {isOpen && <div dangerouslySetInnerHTML={{ __html: content }} />}
      </div>
    );
  }

  return (
    <div className="App">
      <label htmlFor="customerLog">Customer Log File:</label><br/>
      <input type="file" id="customerLog" onChange={(event) => handleFileUpload(event, setCustomerLog)} /><br/>

      <label htmlFor="defaultLog">Default Log File:</label><br/>
      <input type="file" id="defaultLog" onChange={(event) => handleFileUpload(event, setDefaultLog)} />

      <button onClick={handleSubmit}>Submit</button>

      {output1 && <Collapsible title="Output 1" content={output1} />}
      {output2 && <Collapsible title="Output 2" content={output2} />}
    </div>
  );
}

export default App;
