// App.js
import React, { useState } from 'react';
import './App.css';
import AccelChart from './AccelChart'; // Import the AccelChart component

function App() {
  const [selectedContent, setSelectedContent] = useState('');
  const [results, setResults] = useState({
    battery_status_dict: null,
    gps_status_dict: null,
    accel_status_dict: null,
  });
  const [customerLog, setCustomerLog] = useState(null);
  const [fileName, setFileName] = useState(''); // New state variable to store the filename
  const [accelData, setAccelData] = useState({ accel_x_hover: [], accel_y_hover: [], accel_z_hover: [] }); // New state variable to store the accelerometer data

  const handleFileUpload = async (event) => {
    if (event.target.files && event.target.files.length > 0) { // Check if any file is selected
      const file = event.target.files[0];
      setFileName(file.name); // Update the state with the file name
      const formData = new FormData();
      formData.append('customerLog', file);

      try {
        const response = await fetch('http://localhost:5000/upload', {
          method: 'POST',
          body: formData,
        });

        if (!response.ok) throw new Error('Network response was not ok.');

        const data = await response.json();
        setResults(data);
        setAccelData({ // Set the accelerometer data
          acc_x_hover: data.acc_x_hover,
          acc_y_hover: data.acc_y_hover,
          acc_z_hover: data.acc_z_hover
        });
      } catch (error) {
        console.error('Error during fetch:', error);
      }
    } else {
      // Handle the case where no file is selected
    }
  };

  const handleButtonClick = (contentKey) => {
    setSelectedContent(contentKey);
  };

  // Dynamically generate buttons based on results keys
  const tabs = Object.keys(results).filter(key => results[key] !== null).map((key) => (
    <button key={key} onClick={() => handleButtonClick(key)}>
      {key.replace('_', ' ')}
    </button>
  ));

  // Display the selected content
  const displayContent = selectedContent ? JSON.stringify(results[selectedContent], null, 2) : '';

  return (
    <div className="App">
      <div className="top-section">
        <button onClick={() => document.getElementById('fileInput').click()}>Upload and Process ULog File</button>
        <input type="file" id="fileInput" onChange={handleFileUpload} style={{display: 'none'}} />
        {fileName && ( // This line checks if fileName is not empty
          <span className="filename-display">Uploaded File: {fileName}</span>
        )}
      </div>
      <div className="sidebar">
        {tabs}
      </div>
      {selectedContent && ( // This line checks if selectedContent is not empty
        <div className="content">
          <pre>{displayContent}</pre>
        </div>
      )}
      <AccelChart accelData={accelData} /> // Include the AccelChart component
    </div>
  );
}

export default App;
