// App.js
import React, { useState } from 'react';
import './App.css';
import KeyValueTable from './components/KeyValueTable';

function App() {
  const [results, setResults] = useState({});
  const [selectedContent, setSelectedContent] = useState(null);
  const [fileName, setFileName] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const handleFileUpload = async (event) => {
    if (event.target.files && event.target.files.length > 0) {
      const file = event.target.files[0];
      setFileName(file.name);
      const formData = new FormData();
      formData.append('customerLog', file);

      try {
        const response = await fetch('http://localhost:5000/upload', {
          method: 'POST',
          body: formData,
        });

        if (!response.ok) throw new Error('Network response was not ok.');

        const data = await response.json();
        setResults(data);
      } catch (error) {
        console.error('Error during fetch:', error);
      }
    }
  };

  const handleButtonClick = (contentKey) => {
    const contentValue = results[contentKey];
    setSelectedContent(contentValue);
  };

  const getStatusColor = (status) => {
    return status ? 'green' : 'red';
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="App">
      <div className="top-section">
        <button onClick={() => document.getElementById('fileInput').click()}>Upload and Process ULog File</button>
        <input type="file" id="fileInput" onChange={handleFileUpload} style={{ display: 'none' }} />
        {fileName && <span className="filename-display">Uploaded File: {fileName}</span>}
      </div>
      <div className="main-content">
        <div className={`sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
          <button className="toggle-button" onClick={toggleSidebar}>
            {isSidebarOpen ? 'Collapse' : 'Expand'}
          </button>
          {isSidebarOpen && Object.keys(results).map((key) => (
            <button
              key={key}
              onClick={() => handleButtonClick(key)}
              style={{ backgroundColor: getStatusColor(results[key].status) }}
            >
              {key.replace(/_/g, ' ')}
            </button>
          ))}
        </div>
        <div className="content">
          {selectedContent && <KeyValueTable data={selectedContent} />}
        </div>
      </div>
    </div>
  );
}

export default App;
